const getBase64 = file => {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });
}

document.addEventListener('DOMContentLoaded', event => {

    document.querySelectorAll(`[prevent="true"]`).forEach(element => element.addEventListener('click', event => event.preventDefault()));

    var github_repo_link = document.createElement('a');
    github_repo_link.id = "github_link";
    github_repo_link.target = "_blank";
    github_repo_link.href = "https://github.com/msfpt/file-to-base64-url/";
    github_repo_link.title = "Github - MsfPt";

    fetch("data:image/png;base64,")
        .then(res => res.blob()) // Gets the response and returns it as a blob
        .then(blob => {
            // Here's where you get access to the blob
            // And you can use it for whatever you want
            // Like calling ref().put(blob)

            // Here, I use it to make an image appear on the page
            let objectURL = URL.createObjectURL(blob);

            var github_mark = new Image(32);
            github_mark.loading = "lazy";
            github_mark.src = objectURL;
            github_mark.title = "View in Github";
            github_mark.alt = github_mark.title;

            github_repo_link.appendChild(github_mark);

        });
    
    const style_github_link = document.createElement('style');

    const head = document.head || document.getElementsByTagName('head')[0];

    head.appendChild(style_github_link);    

    style_github_link.type = 'text/css';

    var css_style_github_link = "#github_link { filter: drop-shadow(0 0 4px rgba(120 120 120 / 25%));position: fixed;display: block;top: 20px;right: 20px;z-index: 46; } #github_link:hover { opacity: 0.9; }";

    if (style_github_link.styleSheet) {
        style_github_link.styleSheet.cssText = css_style_github_link;
    } else {
        style_github_link.appendChild(document.createTextNode(css_style_github_link));
    }

    document.body.appendChild(github_repo_link);

    const form = document.querySelector('#form');

    const inputFile = document.querySelector('#file');

    const dataBox = document.querySelector('#show-data');

    form.addEventListener('change', event => {

        if (inputFile.files.length > 0) {

            let file = inputFile.files[0];

            dataBox.removeAttribute("hide");

            getBase64(file).then(url => {
                dataBox.querySelector("h3[file-name]").innerHTML = (file.name ? file.name : "Unknown");
                dataBox.querySelector("div[base64-url]").innerHTML = (url ? url : "Unknown");
            });

        };

    });

    form.addEventListener('submit', event => event.preventDefault());

    const reset = event => {

        dataBox.setAttribute('hide', 'true');

        dataBox.querySelector("h3[file-name]").innerHTML = "Loading...";

        dataBox.querySelector("div[base64-url]").innerHTML = "Loading...";

        form.reset();

    }

    const close_btn = dataBox.querySelector('#close');

    close_btn.addEventListener("click", reset);

});